<html> 
<body>
<h3><u>Customer Inspection Report</u></h3><br>
 <table width=100% style="line-height:0.9;">
    <tr width=100%> 
         <td width=25%>Customer Code:</td>
         <td width=25%>{{ doc.customer_code }}</td>
         <td width=25%>Job No:</td>
         <td width=25%>{{ doc.job_no }}</td>
    </tr>
    <tr width=100%>
         <td width=25%>Part Name:</td>
         <td width=25%>{{ doc.part_name }}</td>
         <td width=25%>Po No:</td>
         <td width=25%>{{ doc.po_no }}</td>    
    </tr>
    <tr width=100%>
        <td width=25%>Drawing No:</td>
        <td width=25%>{{ doc.drawing_no }}</td> 
        <td width=25%>Quantity:</td>
        <td width=25%>{{ doc.quantity }}</td>
   </tr>  
   <tr width=100%>
        <td width=25%>Part No:</td> 
        <td width=25%>{{ doc.part_no }}</td>
    </tr>
</table>
{% set count=1 %}
{% set q_count2=1 %}
{%- for d in range(0,doc.r_qty) %}
<table class="table table-bordered" style="table-layout: fixed;">
	<tbody>
		<tr width="100%">
			<th rowspan="2" style="text-align:center;width:5%">Sr</th>
			<th rowspan="2" style="text-align:center;width:12%">Dimension</th>
			<th colspan="2" rowspan="2" style="text-align:center;width:15%">Tolerence</th>
                        <th colspan="5" style="text-align:center;width:75%">Actual Measurement</th>
                 </tr>
                 <tr>
                        {%- for i in range(0,5) %}
                           {% if count <= doc.quantity|int %}
                             <th style="text-align:center;" width="15%">{{ count }}</th>
                           {% endif %}
                        {% set count = count+1 %} 
                        {% endfor %}
               </tr>
               {%- for r in doc.measurements %}
                <tr>
			<td style="text-align:center;">{{ r.idx }}</td>
			<td style="text-align:center;width:12%">{{ r.dimension or "" }}</td>
			<td style="text-align:center;width:7.5%">{{ r.tolerance_to or ""}}</td>
                        <td style="text-align:center;width:7.5%">{{ r.tolerance_from or ""}}</td>
                        {% if r.actual_mesurement %}
                        {% set a=frappe.get_doc('Actual Mesurement',r.actual_mesurement) %}
                        {% set w=a.get('mesurement_values') %}
                        {%- for i in range(0,5) %}
			     {% if count <= doc.quantity|int %}
                               <td style="text-align:center;word-wrap:break-word">{{ w[count-1].value or "" }}</td>
                             {% endif %}
                          {% set count = count+1 %}
                        {% endfor %}
                        {% else %}
                            {%- for i in range(0,5) %}
			      {% if count <= doc.quantity|int %}
                                <td style="text-align:center;word-wrap:break-word" width="15%">{{ w[count-1].value or "" }}</td>
                              {% endif %}
                              {% set count = count+1 %}
                            {% endfor %}
                        {% endif %}
		</tr>
                {% endfor %}
                <tr>
                         <th colspan="4" style="text-align:center;">Heat</th>
                         {% set h=doc.get('heat_no_details') %}
                         {%- for i in range(0,5) %}
            		    {% if count <= doc.quantity|int %}
                               <td style="text-align:center;word-wrap:break-word" width="15%">{{ h[count-1].heat_no or "" }}</td>
                             {% endif %}
                          {% set count = count+1 %}
                        {% endfor %}
                </tr>

                {% set count = count+5 %}


	</tbody>
</table>
{% endfor -%}

<div>
       <table width=100%>
              <tbody>
                     <tr width=100%>
                         <td width=20%><b>Inspected By :</b></td>
                         <td width=30%>{{ doc.inspector_name or "" }}</td>
                         <td width=20%><b>Approved By :</b></td>
                         <td width=30%><hr></td>
                    </tr>

                  <tr width=100% height=20px>
                          <td width=20%><b>Signature :</b></td>
                         <td width=30%><hr></td>
                         <td width=20%><b>Signature :</b></td>
                         <td width=30%><hr></td>
                    </tr>
                    <tr width=100% height=20px>
                          <td width=20%><b>Date :</b></td>
                         <td width=30%>{{ doc.date or "" }}</td>
                         <td width=20%><b>Company Stamp :</b></td>
                         <td width=30%><hr></td>
                    </tr>
              </tbody>
       </table>
</div>


</html>
